t.script:
document.addEventListener("DOMContentLoaded", function() {
    var lat = parseFloat(odoo.session_info.context.default_latitude);
    var lon = parseFloat(odoo.session_info.context.default_longitude);

    var map = L.map('gps_map').setView([lat, lon], 15);
    L.tileLayer('https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png', {
        attribution: '© OpenStreetMap contributors'
    }).addTo(map);

    L.marker([lat, lon]).addTo(map)
        .bindPopup("Ubicación Actual del Vehículo")
        .openPopup();
});
